import { execution } from '@dynatrace-sdk/automation-utils';

export default async function ({ executionId }) {
  const userPass = 'ROBOT_' + Math.floor(Math.random() * 10000).toString(16)

  let response = await fetch("https://unguard.e6cd9768-62ab-406f-992c-1393f2d7a706.dynatrace.training/ui/login", {
    "body": "username=" + userPass + "&password=" + userPass + "&login=",
    "headers": {
        "Content-Type": "application/x-www-form-urlencoded",
    },
    "method": "POST",
});
  return await response.status;
}
